FROM test_data_child a

WHERE a.id = #{id}

SELECT 

FROM test_data_child a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.start = #{start.id}


		AND a.end = #{end.id}


		AND a.test_data_main_id = #{testDataMain.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM test_data_child a


	a.del_flag = #{DEL_FLAG_NORMAL}



