mapper.update:
UPDATE test_data_child SET 	
	start = #{start.id},
	end = #{end.id},
	price = #{price},
	test_data_main_id = #{testDataMain.id},
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

DELETE FROM test_data_child


		WHERE id = #{id}


		WHERE test_data_main_id = #{testDataMain.id}

UPDATE test_data_child SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE test_data_main_id = #{testDataMain.id}
